const fullNameAnswer = $node["cadastro_na_lp"].json["body"]["name"] || "";
const emailAnswer = $node["cadastro_na_lp"].json["body"]["Digite_seu_melhor_e_mail"] || "";
const telefoneClienteOriginal = $node["cadastro_na_lp"].json["body"]["DDD_Celular_Opcional"] || "";
const instagramAnswer = $node["cadastro_na_lp"].json["body"]["Instagram"] || "";

function capitalizeName(fullName) {
    if (typeof fullName === 'undefined') {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    let words = fullName.toLowerCase().split(' ').map((word, index) => {
        if (lowerCaseWords.includes(word) && index !== 0) {
            return word;
        }
        return word.charAt(0).toUpperCase() + word.slice(1);
    });

    let fname = words.shift();
    let lname = words.join(' ');

    // Ajuste para remover o espa칞o extra caso n칚o haja sobrenome
    let fullname = lname ? `${fname} ${lname}` : fname;

    return { fname, lname, fullname };
}

function formatEmail(email) {
    return email.toLowerCase().trim();
}

function formatInstagram(instagram) {
    // Remover "@" e transformar em letras min칰sculas
    return instagram.toLowerCase().replace(/^@/, "");
}

let telefoneFinal = "";

// Verificar se o telefone foi preenchido
if (telefoneClienteOriginal) {
    // Limpar o n칰mero para remover caracteres n칚o num칠ricos
    const telefoneLimpo = telefoneClienteOriginal.replace(/\D/g, "");

    let DDI = '';
    let DDD = '';
    let numero = '';

    // Determinar se o telefone possui DDI
    if (telefoneLimpo.length > 11) {
        // Extrair o DDI (primeiros dois d칤gitos)
        DDI = telefoneLimpo.slice(0, 2);
        DDD = telefoneLimpo.slice(2, 4);
        numero = telefoneLimpo.slice(4);
    } else if (telefoneLimpo.length === 11) {
        // Assumir DDI padr칚o (55) para n칰meros com DDD + n칰mero
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else if (telefoneLimpo.length === 10) {
        // Assumir DDI padr칚o (55) para n칰meros com DDD + n칰mero de 10 d칤gitos
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else {
        // Caso o n칰mero n칚o tenha tamanho esperado, consider치-lo inv치lido
        telefoneFinal = telefoneLimpo; // Ou deixar vazio: telefoneFinal = "";
    }

    // Ajustar n칰mero com base no DDD
    if (DDD && numero) {
        if (parseInt(DDD) > 28) {
            // Se o DDD for maior que 28 e o n칰mero tiver 9 d칤gitos, remover o nono d칤gito
            if (numero.length === 9 && numero.startsWith("9")) {
                numero = numero.slice(1); // Remove o primeiro d칤gito
            }
        } else {
            // Se o DDD for menor ou igual a 28 e o n칰mero tiver 8 d칤gitos, adicionar o nono d칤gito
            if (numero.length === 8) {
                numero = `9${numero}`; // Adiciona o nono d칤gito
            }
        }
    }

    // Formatar n칰mero final apenas se v치lido
    if (DDI && DDD && numero) {
        telefoneFinal = `${DDI}${DDD}${numero}`;
    }
}

const formattedNames = capitalizeName(fullNameAnswer);
const formattedEmail = formatEmail(emailAnswer);
const formattedInstagram = formatInstagram(instagramAnswer);
const utm_source = $node["cadastro_na_lp"].json["body"]["UTM_SOURCE"] || "";
const utm_campaign = $node["cadastro_na_lp"].json["body"]["UTM_CAMPAIGN"] || "";
const utm_medium = $node["cadastro_na_lp"].json["body"]["UTM_MEDIUM"] || "";
const utm_term = $node["cadastro_na_lp"].json["body"]["UTM_TERM"] || "";
const utm_content = $node["cadastro_na_lp"].json["body"]["UTM_CONTENT"] || "";
const form_id = $node["cadastro_na_lp"].json["body"]["Id_do_formulario"] || "";
const form_name = "Cadastro Webin치rio" || ""; // Captura o form_name
const link_pagina = $node["cadastro_na_lp"].json["body"]["URL"];

const result = {
    ...formattedNames, // Nome formatado
    email: formattedEmail, // Email formatado
    whatsapp: telefoneFinal, // WhatsApp formatado ou vazio
    instagram: formattedInstagram, // Instagram formatado
    utm_source: utm_source,
    utm_campaign: utm_campaign,
    utm_term: utm_term,
    utm_medium: utm_medium,
    utm_content: utm_content,
    form_id: form_id,
    link_pagina: link_pagina,
    form_name: form_name // Inclui o form_name no resultado
};

// Aqui estamos ajustando a estrutura do retorno para se adequar ao que o n8n espera
return [{ json: result }];
