const perguntas_respostas = {};

// Função para extrair múltiplas respostas e juntá-las por vírgula
function extractMultipleChoices(answerId) {
    const resposta = respostas.find(answer => answer.field.ref === answerId);
    if (resposta && Array.isArray(resposta.choice?.label)) {
        const respostaFormatada = resposta.choice.label.map(choice => choice.label).join(', ');
        perguntas_respostas[answerId] = respostaFormatada;
        return respostaFormatada;
    }
    return '';
}

