// Data de aplicação
const dataAplicacao = inputData.body.form_response.submitted_at || '';

// Função para obter a data atual menos 15 dias no formato brasileiro (dia/mês/ano)
function getDataMenos15Dias() {
  const dataAtual = new Date();
  dataAtual.setDate(dataAtual.getDate() - 15);
  const dia = dataAtual.getDate().toString().padStart(2, '0');
  const mes = (dataAtual.getMonth() + 1).toString().padStart(2, '0'); // +1 porque o mês começa em 0
  const ano = dataAtual.getFullYear();
  return `${ano}-${mes}-${dia}`;
}

function formatarDataISO(dataISO) {
    const data = new Date(dataISO);

    const ano = data.getFullYear();
    const mes = String(data.getMonth() + 1).padStart(2, '0'); // Mês começa do zero
    const dia = String(data.getDate()).padStart(2, '0');
    const horas = String(data.getHours()).padStart(2, '0');
    const minutos = String(data.getMinutes()).padStart(2, '0');
    const segundos = String(data.getSeconds()).padStart(2, '0');

    return `${ano}-${mes}-${dia} ${horas}:${minutos}:${segundos}`;
}

// Função para extrair as UTMs do objeto JSON
function extractUTMs(json) {
  const utmParams = {};

  function searchUTMs(obj) {
    for (const key in obj) {
      if (key.startsWith('utm_')) {
        utmParams[key] = obj[key];
      } else if (typeof obj[key] === 'object') {
        searchUTMs(obj[key]);
      }
    }
  }



  searchUTMs(json);

  return utmParams;
}


// Extrair as UTMs do objeto JSON de entrada
const utms = extractUTMs(inputData);

// UTMS
