const utmSource = utms.utm_source || '';
const utmCampaign = utms.utm_campaign || '';
const utmMedium = utms.utm_medium || '';
const utmTerm = utms.utm_term || '';

// Função para remover todas as aspas e parênteses de um texto
function removerAspasEParenteses(texto) {
    // Substitui todas as ocorrências de aspas ("") por nada
    let textoSemAspas = texto.replace(/"/g, '');
    // Substitui todas as ocorrências de parênteses () por nada
    textoSemAspas = textoSemAspas.replace(/\([^()]*\)/g, '');
    return textoSemAspas;
}

function capitalizeName(fullName) {
    if (typeof fullName === 'undefined') {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    let words = fullName.toLowerCase().split(' ').map((word, index) => {
        if (lowerCaseWords.includes(word) && index !== 0) {
            return word;
        }
        return word.charAt(0).toUpperCase() + word.slice(1);
    });

    let fname = words.shift();
    let lname = words.join(' ');

    // Ajuste para remover o espaço extra caso não haja sobrenome
    let fullname = lname ? `${fname} ${lname}` : fname;

    return { fname, lname, fullname };
}

function formatEmail(email) {
    return email.toLowerCase().trim();
}

function formatInstagram(instagram) {
    // Remover "@" e transformar em letras minúsculas
    return instagram.toLowerCase().trim().replace(/^@/, "");
}

let telefoneFinal = "";

// Verificar se o telefone foi preenchido
if (telefoneClienteOriginal) {
    // Limpar o número para remover caracteres não numéricos
    const telefoneLimpo = telefoneClienteOriginal.replace(/\D/g, "");

    let DDI = '';
    let DDD = '';
    let numero = '';

    // Determinar se o telefone possui DDI
    if (telefoneLimpo.length > 11) {
        // Extrair o DDI (primeiros dois dígitos)
        DDI = telefoneLimpo.slice(0, 2);
        DDD = telefoneLimpo.slice(2, 4);
        numero = telefoneLimpo.slice(4);
    } else if (telefoneLimpo.length === 11) {
        // Assumir DDI padrão (55) para números com DDD + número
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else if (telefoneLimpo.length === 10) {
        // Assumir DDI padrão (55) para números com DDD + número de 10 dígitos
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else {
        // Caso o número não tenha tamanho esperado, considerá-lo inválido
        telefoneFinal = telefoneLimpo; // Ou deixar vazio: telefoneFinal = "";
    }

    // Ajustar número com base no DDD
    if (DDD && numero) {
        if (parseInt(DDD) > 28) {
            // Se o DDD for maior que 28 e o número tiver 9 dígitos, remover o nono dígito
            if (numero.length === 9 && numero.startsWith("9")) {
                numero = numero.slice(1); // Remove o primeiro dígito
            }
        } else {
            // Se o DDD for menor ou igual a 28 e o número tiver 8 dígitos, adicionar o nono dígito
            if (numero.length === 8) {
                numero = `9${numero}`; // Adiciona o nono dígito
