//Formatar n√∫mero Internacional
function formatarTelefone(telefone) {
  const telefoneLimpo = telefone.replace(/[^\d]/g, '');

  let ddi;
  let ddd;
  let telefoneFormatado;

  // Verificar se o telefone come√ßa com "55" antes de format√°-lo
  if (telefoneLimpo.startsWith('55')) {
    ddi = telefoneLimpo.slice(0, 2);
    ddd = telefoneLimpo.slice(2, 4);
    if (parseInt(ddd, 10) > 28) {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
    } else {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
    }
  } else {
    // Se o DDI for diferente de 55, manter o n√∫mero como est√°
    telefoneFormatado = telefoneLimpo;
  }

  return telefoneFormatado;
}


// Fun√ß√£o para obter a data atual menos 15 dias no formato brasileiro (dia/m√™s/ano)
function getDataMenos15Dias() {
  const dataAtual = new Date();
  dataAtual.setDate(dataAtual.getDate() - 15);
  const dia = dataAtual.getDate().toString().padStart(2, '0');
  const mes = (dataAtual.getMonth() + 1).toString().padStart(2, '0'); // +1 porque o m√™s come√ßa em 0
  const ano = dataAtual.getFullYear();
  return `${ano}-${mes}-${dia}`;
}

