// Fun√ß√£o para extrair as UTMs do objeto JSON
function extractUTMs(json) {
  const utmParams = {};

  function searchUTMs(obj) {
    for (const key in obj) {
      if (key.startsWith('utm_')) {
        utmParams[key] = obj[key];
      } else if (typeof obj[key] === 'object') {
        searchUTMs(obj[key]);
      }
    }
  }

  searchUTMs(json);

  return utmParams;
}

